<?php

namespace App\GuestbookBundle\Repository;

/**
 * NumberRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class NumberRepository extends \Doctrine\ORM\EntityRepository
{
	public function get_numbers($user_id, $number){
		$dql = "SELECT n FROM AppGuestbookBundle:Number n
			WHERE n.user = :user OR n.number = :number";

		$query = $this->getEntityManager()->createQuery($dql)
			->SetParameter('user', $user_id)
			->SetParameter('number', $number);

		$result = $query->execute();
		return $result;
	}

